package Basics;

public class AssignnOper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 43;
		System.out.println("After using = Assignment operator " + num);
// Test case 1:		
		num += 32;
		System.out.println("After using += Assignment operator " + num);
		
		num -= 32;
		System.out.println("After using -= Assignment operator " + num);
		
		num *= 32;
		System.out.println("After using *= Assignment operator " + num);
		
		num /= 32;
		System.out.println("After using /= Assignment operator " + num);
		
		num %= 32;
		System.out.println("After using %= Assignment operator " + num);
// Test Case 2 :
		num += 21;
		System.out.println("After using += Assignment operator " + num);
		
		num -= 98;
		System.out.println("After using -= Assignment operator " + num);
		
		num *= 98;
		System.out.println("After using *= Assignment operator " + num);
		
		num /= 9;
		System.out.println("After using /= Assignment operator " + num);
		
		num %= 32;
		System.out.println("After using %= Assignment operator " + num);
		
		
		
		
	}

}
